    private void btnCrearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearActionPerformed
        String Name = txtNombre.getText();
        long Number;
        if (txtNumero.getText().isEmpty() || !Func.isNumeric(txtNumero.getText())){
            Number = 0;
        }
        else{Number = Long.parseLong(txtNumero.getText());}
        Func archivo = new Func("C:\\Users\\Public\\Documents\\poo\\Contacts.txt");
        JOptionPane.showMessageDialog(this, archivo.crear(Name, Number));
        txtNombre.setText("");
        txtNumero.setText("");
    }//GEN-LAST:event_btnCrearActionPerformed

    private void btnActualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnActualizarActionPerformed
        String Name = txtNombre.getText();
        long NNumber;
        if (txtNumero.getText().isEmpty() || !Func.isNumeric(txtNumero.getText())){
            NNumber = 0;
        }
        else{NNumber = Long.parseLong(txtNumero.getText());}
        Func archivo = new Func("C:\\Users\\Public\\Documents\\poo\\Contacts.txt");
        JOptionPane.showMessageDialog(this, archivo.actualizar(Name, NNumber));
        txtNombre.setText("");
        txtNumero.setText("");
    }//GEN-LAST:event_btnActualizarActionPerformed

    private void btnBorrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBorrarActionPerformed
        String Name = txtNombre.getText();
        Func archivo = new Func("C:\\Users\\Public\\Documents\\poo\\Contacts.txt");
        JOptionPane.showMessageDialog(this, archivo.borrar(Name));
        txtNombre.setText("");
        txtNumero.setText("");
    }//GEN-LAST:event_btnBorrarActionPerformed

